import { StyleSheet, Text, View, Image, TouchableOpacity, FlatList, ScrollView, SafeAreaView,CheckBox  } from 'react-native';
import React, { useEffect, useState } from 'react';
import databike from '../data/databike';
import {TextInput } from 'react-native-paper';
import Icon from 'react-native-vector-icons/FontAwesome';
export default function Screen02({navigation,route}) {
    const [filter, setFilter] = useState('All');
    const [filteredData, setFilteredData] = useState(databike);
    const [search,setSearch] = useState('');
    const [checkboxes, setCheckboxes] = useState([
        { label: 'To check email', value: false },
        { label: 'UI task web page', value: false },
        { label: 'Learn javascript basic', value: false },
        { label: 'Learn HTML Advance', value: false },
        { label: 'Medical App UI', value: false },
        { label: 'Learn Java', value: false },
     
      ]);

    const handleFilter = (FilterType) => {
        setFilter(FilterType);
        if (FilterType === 'All') {
            setFilteredData(databike);
        } else {
            setFilteredData(databike.filter((item) => item.type === FilterType));
        }
    };
    const handleCheckboxChange = (index) => {
        const updatedCheckboxes = [...checkboxes];
        updatedCheckboxes[index].value = !updatedCheckboxes[index].value;
        setCheckboxes(updatedCheckboxes);
      }
    return (
        <View style={styles.container}>
            <SafeAreaView>
                <TextInput
                    placeholder='Search'
                    style={styles.txtEmail}
                    value={search}
                    onChangeText={text=> setSearch(text)}
                    left={<TextInput.Icon icon="search" />}
                    keyboardType='default'
                />
            </SafeAreaView>
            <SafeAreaView style={styles.txtTitle}>
                <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center'}}>
                    {checkboxes.map((checkbox, index) => (
                    <View key={index} style={{ flexDirection: 'row', alignItems: 'center' ,width:'300px',padding: 10,backgroundColor:'silver',borderRadius:10,margin:5,position:'relative'}}>
                        <CheckBox
                            value={checkbox.value}
                            onValueChange={() => handleCheckboxChange(index)}
                        />
                        <Text style={{fontSize:15,left:30}}>{checkbox.label}</Text>
                        <TouchableOpacity onPress={() =>{
                            navigation.navigate('Screen01');
                        }}>
                        <Icon style={{left:180}} name="edit" size={15} color="#900" />
                        </TouchableOpacity>
                    </View>
                    ))}
                </View>
            </SafeAreaView> 
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        justifyContent: 'flex-start',
        alignItems: 'center',
    },
    txtTitle: {
        top:50,
        
    },txtEmail:{
        borderColor:'black',
        borderWidth:'1px',
        width:'300px',
        backgroundColor:'white',
        height:40
    }
    ,
    containerFilter: {
        width: '100%',
        flexDirection: 'row',
        justifyContent: 'space-around',
        alignItems: 'center',
        marginTop: 10,
    },
    txtFilter: {
        width: 120,
        height: 20,
        fontFamily: 'Ubuntu',
        fontSize: 15,
        textAlign: 'center',
        fontWeight: 'bold',
        borderWidth: 1,
        borderColor: '#E94141',
        padding: 2,
        color: '#BEB6B6',
    },
    selectedFilter: {
        color: '#E94141',
    },
    item: {
        flex: 1,
        backgroundColor: 'rgba(247, 186, 131, 0.15)',
        margin: 7,
        width: '100%',
        height: 220,
        justifyContent: 'space-around',
        alignItems: 'center',
    },
    img: {
        width: '85%',
        height: 135,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 10,
    },
    infoSp: {
        alignItems: 'center',
        padding: 10,
        justifyContent: 'flex-end',
    },
    nameSp: {
        fontFamily: 'Ubuntu',
        fontSize: 20,
        textAlign: 'center',
        color: '#000',
    },
    priceSp: {
        fontFamily: 'Ubuntu',
        fontSize: 20,
        textAlign: 'center',
        color: '#E94141',
    },
    buttonLike: {
        width: 30,
        height: 30,
        position: 'absolute',
        alignSelf: 'flex-start',
        marginTop: -170,
        marginLeft: 10,
    },
    like: {
        width: 30,
        height: 30,
    }
});
