import { StyleSheet, Text, View, Image, TouchableOpacity, FlatList, ScrollView, SafeAreaView,CheckBox  } from 'react-native';
import React, { useEffect, useState } from 'react';
import databike from '../data/databike';
import {TextInput } from 'react-native-paper';
import Icon from 'react-native-vector-icons/FontAwesome';
import { Button } from 'react-native';
export default function Screen02({navigation,route}) {
    const [filter, setFilter] = useState('All');
    const [filteredData, setFilteredData] = useState(databike);
    const [search,setSearch] = useState('');
    const [checkboxes, setCheckboxes] = useState([
        { label: 'To check email', value: false },
        { label: 'UI task web page', value: false },
        { label: 'Learn javascript basic', value: false },
        { label: 'Learn HTML Advance', value: false },
        { label: 'Medical App UI', value: false },
        { label: 'Learn Java', value: false },
     
      ]);

    const handleFilter = (FilterType) => {
        setFilter(FilterType);
        if (FilterType === 'All') {
            setFilteredData(databike);
        } else {
            setFilteredData(databike.filter((item) => item.type === FilterType));
        }
    };
    const handleCheckboxChange = (index) => {
        const updatedCheckboxes = [...checkboxes];
        updatedCheckboxes[index].value = !updatedCheckboxes[index].value;
        setCheckboxes(updatedCheckboxes);
      }
    return (
        <View style={styles.container}>
            <SafeAreaView>
                <TextInput
                    placeholder='Search'
                    style={styles.txtEmail}
                    value={search}
                    onChangeText={text=> setSearch(text)}
                    left={<TextInput.Icon icon="search" />}
                    keyboardType='default'
                />
            </SafeAreaView>
            <SafeAreaView style={styles.txtTitle}>
                <View style={{ flex: 2}}>
                    {checkboxes.map((checkbox, index) => (
                    <View key={index} style={{flexDirection: 'row',width:'300px',padding: 10,backgroundColor:'silver',borderRadius:10,margin:5,position:'relative'}}>
                        <CheckBox
                            value={checkbox.value}
                            onValueChange={() => handleCheckboxChange(index)}
                        />
                        <Text style={{fontSize:15,left:30,width:150}}>{checkbox.label}</Text>
                        <TouchableOpacity onPress={() =>{
                            navigation.navigate('Screen01');
                        }}>
                        <Icon style={{position:'absolute',left:100}} name="edit" size={15} color="#900" />
                        </TouchableOpacity>
                    </View>
                    
                    ))}
                </View>
            </SafeAreaView> 
            <TouchableOpacity style={styles.buttonStart} 
                onPress={() =>{
                    navigation.navigate('Screen02',{email});
                }}
                >
                <Text style={styles.txtButton}>+</Text>
            </TouchableOpacity>
            <View style={{backgroundColor:'blue',width:100,height:100,justifyContent:'center'}}>
                <Button
                    title='+' 
                />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        justifyContent: 'space-around',
        alignItems: 'center',
    },
    txtTitle: {
        top:50,
        
    },txtEmail:{
        borderColor:'black',
        borderWidth:'1px',
        width:'300px',
        backgroundColor:'white',
        height:40
    },
    buttonStart:{
        width:75,
        height:100,
        justifyContent:'center',
        backgroundColor:'red',
    },
    txtButton:{
        fontSize:25,
        padding:15,
        borderRadius:'90px',
        backgroundColor:'#00BDD6',
        color:'white',
        textAlign:'center'
    },
   
});
