import { StyleSheet, Text, View, Image, TouchableOpacity} from 'react-native';
import React, { useEffect, useState } from 'react';
export default function Screen01({navigation}) {
    return (
        <View style={styles.container}>
            <View style={styles.containerTitle}>
                <Text style={styles.txtTitle}>
                    A premium online store for <br/> 
                    sporter and their stylish choice
                </Text>
            </View>
            <View style={styles.containerImgBike}>
                <Image style={{ width: '300px',height: '200px',}}
                    source={require('../assets/Image 95.png')} />
            </View>
            <Text style={styles.txtShopName}>POWER BIKE <br/> 
                    SHOP</Text>
            <TouchableOpacity style={styles.buttonStart} 
                onPress={() =>{
                    navigation.navigate('Hinh2');
                }}
                >
                <Text style={styles.txtButton} >Get Started &rarr;</Text>
            </TouchableOpacity>
        </View>
    );
}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        justifyContent: 'space-around',
        alignItems: 'center',
    },txtButton:{
        fontSize:25,
        padding: 25,
        borderRadius:30,
        backgroundColor:'#00BDD6',
        color:'white',
    },
});
